import React, { useState } from 'react'

export default function useHookes() {
    const [items , setItems] = useState([]);
    const [employee, setEmployees] = useState("");

    const getShowName = () => {
        setItems([
            ...items,
            {
                id:items.length,
                name: employee,
            }
        ]);
        setEmployees("")
    }
  return (
    <div>
      <div className="App">
        <h1>Affiche Names</h1>
        <input type="text" name="" id="" 
            value={employee}
            onChange={(e)=>setEmployees(e.target.value)}
            placeholder='Enter Your Name ...'
        />
        <button onClick={()=> getShowName()}>Get Name</button>
        <ul>
            {items.map((item)=>{
                    <li key={item.id}>{item.name}</li>
                })
            }
        </ul>
      </div>
    </div>
  )
}
