import React,{useState} from 'react';

function Headers() {
  const [items, setItems] = useState([]);
  const [employees, setEmployees] = useState("")

  const showName = () => {
    setItems([
      ...items,
      {
        id:items.length,
        name: employes
      },
    ]);
    setEmployees("");
  }

  return (
    <div>
      <h1>Form UseState</h1>
      <form action="">
        <input type="text" 
          value={employes}
          onChange={(e)=> setEmployes(e.target.value)}
        />
        <button onClick={()=>showName()}>Get Name</button>
      </form>
      <ul>
        {items.map((item)=>{
          <li key={item.id}>{item.name}</li>
        })}
      </ul>
    </div>
  )
}

export default Headers
