import React, { useState } from 'react';

function Headers() {
  const [items, setItems] = useState([]);
  const [employees, setEmployees] = useState("");

  const showName = () => {
    setItems([
      ...items,
      {
        id: items.length,
        name: employees
      },
    ]);
    setEmployees(""); // Clear the input field after adding the name
  }

  return (
    <div>
      <h1>Form UseState</h1>
      <form action="">
        <input type="text" 
          value={employees}
          onChange={(e) => setEmployees(e.target.value)}
        />
        <button type="button" onClick={() => showName()}>Get Name</button>
      </form>
      <ul>
        {items.map((item) => (
          <li key={item.id}>{item.name}</li>
        ))}
      </ul>
    </div>
  )
}

export default Headers;
